Example: //Java Program to print the array elements using for-each loop
 class Testarray1{
 	public static void main(String args[]){
 		int arr[]={33,3,4,5};
 		//printing array using for-each loop
 		for(int i:arr)
 			System.out.println(i);
 	}
 }

